import React from 'react'
import APIContext from "./APIContext"



const APIProvider = (props) => {

    //set api url based on current url 
    var url = ""
    console.log (window.location.href)
    if (window.location.href.includes ("localhost")){
        url = "http://localhost:3001"
    }else {//will be production
        url = "https://colorsappraheema.herokuapp.com"
    }

    const apiContext = {
        api_url: url ,
        authorization: 'Bearer eyJhbGciOiJSUzI1NiIsInR5cCI6IkpXVCIsImtpZCI6IjZ3TTZRV2w5dHVLM3pLcGFWLWNXNiJ9..k3rVahHuWTqmm_RSN192qZGrXAxMqDwb-OkA8zzSjLTyqikOWTgn6EKktAI4iBdLXbZ37ix49aiOlyUDlamQlwUYQmzvPtIgJakJyJ1bEhDURcie9x_IeJeGoULXB05MkHy7BbzxO8fjB5EXpvkva3Golv7uBU4QrGsz5t7BMgf-01fGgVKhfxIomeGKPo6BI2h2Y0G7xM4B8Un8eXQmPZ1AIQlGJ7orL09GPCpOL_Yq10aZV-yHHkfEMKZ6rU99xRF_GZ6wGKezFVS01y-ORsj8fUCQDwdLyyViL8xyJ5TnoKPJrSQdpnnlG_Jg88xfppXQLejK8F9XEa_m5MFyyg'

    }

    return (
        <APIContext.Provider value={apiContext}>
            {props.children}
        </APIContext.Provider>
    )
}

export default APIProvider;
