import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormHelperText from '@mui/material/FormHelperText';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';
import * as React from 'react';
import ByColorResults from "../ResultsTable/ByColorResults"

export default function SearchByColorForm() {
    const [color, setColor] = React.useState('');
    const [colors, setColors] = React.useState ([])
    const [showTable, setShowTable] = React.useState (false)
    const handleChange = (event) => {
      console.log ("Trying")
      setColor(event.target.value);
    };

    React.useEffect(() => {
      fetch("/api/v1/colors")
        .then((res) => res.json())
        .then((data) => {console.log (data); setColors(data.colors)});
    }, []);

    return (
      <div>
        <FormControl sx={{ m: 1, minWidth: 120 }}>
          <InputLabel id="demo-simple-select-helper-label">Age</InputLabel>
          <Select
            labelId="demo-simple-select-helper-label"
            id="demo-simple-select-helper"
            value={color}
            label="Color"
            onChange={handleChange}
          >
            <MenuItem value="">
              <em>None</em>
            </MenuItem>

            {colors && colors.map (color=> <MenuItem value={color}>{color}</MenuItem>)}
          </Select>
          <FormHelperText>Select a color or none to view all users</FormHelperText>
        </FormControl>
        {showTable && ByColorResults}
      </div>
    );
  }
