import * as React from 'react';
import TextField from '@mui/material/TextField';
import Stack from '@mui/material/Stack';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';

export default function AddUserForm() {

  const [username, setUsername] = React.useState()
  const [group, setGroup] = React.useState()
  const [color, setColor] = React.useState()
  const [response, setResponse] = React.useState()
  const [submitted, setSubmitted] = React.useState (false)
  const [showError, setShowError]  = React.useState (false)
  const submitHandler = () => {
    console.log ("submit handler called")
    setSubmitted(true)
    console.log ("group: "+ group)
    if (group==null || color==null || username==null)
    {
      setShowError (true)
      return; 
    }
    setShowError (false)
    //make sure none of the fields are empty 


  }

  const usernameHandler = (event)=>{
    setUsername (event.target.value.trim())
  }

  const groupHandler = (event)=>{
    setGroup (event.target.value.trim())

  }

  const colorHandler = (event)=>{
    setColor (event.target.value.trim())
  }

  return (
    <Grid container spacing={2}>
      <Grid item xs={12}>

        <form>
          <div>
            {showError? <p>Please enter all fields</p>: <p>Enter information below.</p>}
          </div>
          <Stack direction="row" spacing={2} alignItems="center" justifyContent="center">

            <TextField label="User Name" variant="filled" color={(showError && username==null)? "error":"success"} focused onChange={usernameHandler}/>
            <TextField label="Group" variant="filled" color={(showError && group==null)? "error":"success"} focused onChange={groupHandler}/>
            <TextField label="Color" variant="filled" color={(showError && color==null)? "error":"success"} focused onChange={colorHandler}/>
          </Stack>
          <div style={{ paddingTop: "5px" }}>
            <Button variant="contained" onClick={submitHandler} style={{ backgroundColor: "#65cc66" }}>Submit</Button>
          </div>

        </form>

      </Grid>
      <Grid item xs={12}>
      </Grid>
    </Grid>
  )
}
